package JavaSessions;

public class EvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=14;
		if(a%2==0) {
			System.out.println("The given number is even number: " +a);
		}
		else {
			System.out.println("Given number is not an even number :" +a);
		}
	}

}
